import React, { Component } from 'react';

const withTimeTracking = (WrappedComponent) => {
  class WithTimeTracking extends Component {
    constructor(props) {
      super(props);
      this.state = {
        timeSpent: 0,
      };
      this.startTime = Date.now();
    }

    componentDidMount() {
      this.timer = setInterval(() => {
        this.setState({ timeSpent: Date.now() - this.startTime });
      }, 1000);
    }

    componentWillUnmount() {
      clearInterval(this.timer);
    }

    render() {
      return <WrappedComponent timeSpent={this.state.timeSpent} {...this.props} />;
    }
  }

  return WithTimeTracking;
};

export default withTimeTracking;
