import React, { useContext } from 'react';
import UserSettingsContext from './UserSettingsContext';
import '../css/'; // Import your CSS file

const SettingsForm = () => {
  const { settings, updateSettings } = useContext(UserSettingsContext);

  const handleFormSubmit = (newSettings) => {
    updateSettings(newSettings);
  };

  return (
    <div className="settings-form-container"> {/* Add a container class for styling */}
      <h2>Settings Form</h2>
      <label>
        Theme:
        <select
          value={settings.theme}
          onChange={(e) => handleFormSubmit({ theme: e.target.value })}
        >
          <option value="light">Light</option>
          <option value="dark">Dark</option>
        </select>
      </label>

      <label>
        Language:
        <select
          value={settings.language}
          onChange={(e) => handleFormSubmit({ language: e.target.value })}
        >
          <option value="english">English</option>
          <option value="spanish">Russian</option>
        </select>
      </label>
    </div>
  );
};

export default SettingsForm;
