import React, { useState } from 'react';
import PropTypes from 'prop-types';

const Tooltip = ({ text, position, children }) => {
  const [isVisible, setIsVisible] = useState(false);

  const handleMouseEnter = () => {
    setIsVisible(true);
  };

  const handleMouseLeave = () => {
    setIsVisible(false);
  };

  return (
    <div style={{ position: 'relative', display: 'inline-block' }}>
      <div onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} style={{ display: 'inline-block' }}>
        {children}
      </div>
      {isVisible && (
        <div
          style={{
            position: 'absolute',
            top: position === 'top' ? '-30px' : position === 'bottom' ? '30px' : 0,
            left: position === 'left' ? '-100px' : position === 'right' ? '100px' : 0,
            backgroundColor: '#333',
            color: '#fff',
            padding: '5px',
            borderRadius: '5px',
          }}
        >
          {text}
        </div>
      )}
    </div>
  );
};

Tooltip.propTypes = {
  text: PropTypes.string.isRequired,
  position: PropTypes.oneOf(['top', 'right', 'bottom', 'left']),
  children: PropTypes.node.isRequired,
};

export default Tooltip;
